import React from "react";
import "../styles/index.css";

function CustomArrows({items, currentIndex, nextArrow, prevArrow}) {
  return (
    <div className="custom-container">
      <div>
        <button onClick={nextArrow}>{'<'}</button>
      </div>
      <div className="dots-container">
        {
          items.map((ele, i) => {
            return(
              <div className="dot" style={{backgroundColor: currentIndex === i ? 'pink' : 'grey'}}>
                </div>
            )
          })
        }
      </div>
      <div>
        <button onClick={prevArrow}>{'>'}</button>
      </div>
    </div>
  );
}

export default CustomArrows;
