import React, { useCallback, useEffect, useRef, useState } from "react";
import "../styles/index.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import MenuItem from "../MenuItem";

function Menu() {
  const menuItems = ['Home', 'Electronics', 'Books', 'Music', 'Movies', 'Clothing', 'Games', 'More', 'Furniture', 'Travel', 'Botanical', 'Category Name'];
  const divRef = useRef(null);
  const [width, setWidth] = useState(0);
  const [displayedItems, setDisplayedItems] = useState([]);
  const [hiddenItems, setHiddenItems] = useState([]);
  const menuContainerRef = useRef(null);

  const handleResize = useCallback(() => {
    setWidth(divRef.current.offsetWidth);
  }, []);

  useEffect(() => {
    window.addEventListener('load', handleResize);
    window.addEventListener('resize', handleResize);

    // Initial calculation of menu items
    calculateMenuItems();

    return () => {
      window.removeEventListener('load', handleResize);
      window.removeEventListener('resize', handleResize);
    };
  }, [handleResize]);

  const calculateMenuItems = () => {
    if (menuContainerRef.current) {
      const availableSpace = menuContainerRef.current.offsetWidth;
      const totalWidth = menuRef.current.scrollWidth;
      let itemsToShow = [];
      let itemsToHide = [];

      menuItems.forEach((item) => {
        const itemWidth = getTextWidth(item);

        if (totalWidth + itemWidth <= availableSpace) {
          itemsToShow.push(item);
          totalWidth += itemWidth;
        } else {
          itemsToHide.push(item);
        }
      });

      setDisplayedItems(itemsToShow);
      setHiddenItems(itemsToHide);
    }
  };

  const getTextWidth = (text) => {
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    context.font = '16px Arial'; // You can adjust the font size as needed
    const width = context.measureText(text).width;
    return width;
  };

  return (
    <div className="menu-container" ref={menuContainerRef}>
      <div className="menu">
        <div>
          <h1 className="logo">E-COMM</h1>
        </div>
        <div ref={divRef} className="menu-items">
          {displayedItems.map((ele, i) => (
            <MenuItem key={i} index={i} item={ele} />
          ))}
          {hiddenItems.length > 0 && <div>{'More'}</div>}
        </div>

        <div>
          <div className="search-box">
            <FontAwesomeIcon icon={faMagnifyingGlass} color={'grey'} />
            <input type={'text'} className="search-input" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Menu;
