// src/components/Navigation.js
import React, { useState, useEffect, useRef } from 'react';
import './Navigation.css';

const Navigation = () => {
  const initialMenuItems = [
    "Home", "About", "Services", "Portfolio", "Contact",
  ];

  const [menuItems, setMenuItems] = useState(initialMenuItems);
  const [menuRef, setMenuRef] = useState(null);
  const [moreItems, setMoreItems] = useState([]);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const handleResize = () => {
    setWindowWidth(window.innerWidth);
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    // Calculate available space for menu items
    if (menuRef) {
      const availableSpace = menuRef.offsetWidth;
      const totalWidth = menuRef.scrollWidth;
      const itemsToShow = [];

      initialMenuItems.forEach((item, index) => {
        itemsToShow.push(item);

        const tempItems = [...itemsToShow];
        const tempWidth = tempItems.reduce((width, item) => {
          const itemWidth = getTextWidth(item);
          return width + itemWidth;
        }, 0);

        if (tempWidth >= availableSpace) {
          setMenuItems(itemsToShow);
          setMoreItems(initialMenuItems.slice(index));
          return;
        }
      });

      if (totalWidth <= availableSpace) {
        setMenuItems(initialMenuItems);
        setMoreItems([]);
      }
    }
  }, [windowWidth]);

  const getTextWidth = (text) => {
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    context.font = '16px Arial'; // You can adjust the font size as needed
    const width = context.measureText(text).width;
    return width;
  };

  return (
    <nav className="navigation">
      <ul className="menu" ref={(ref) => setMenuRef(ref)}>
        {menuItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
        {moreItems.length > 0 && (
          <li className="more">
            <span>More</span>
            <ul className="more-menu">
              {moreItems.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navigation;
