import React, { useCallback, useEffect, useRef, useState } from "react";
import "../styles/index.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons'
import MenuItem from "../MenuItem";

function Menu(){
    const menuItems = ['Home', 'Electronics', 'Books', 'Music', 'Movies', 'Clothing', 'Games', 'More', 'Furniture', 'Travel', 'Botanical', 'Category Name']
    const moreItems = ['']
    const divRef = useRef(0)
    const [width, setWidth] = useState(0)
    const [totalSum, setTotalSum] = useState(0)

    const handleResize = useCallback(() => {
        setWidth(divRef.current.offsetWidth)
    }, [divRef])

    useEffect(() => {
        window.addEventListener('load', handleResize)
        window.addEventListener('resize', handleResize)
        console.log(width)

        return () => {
          window.removeEventListener('load', handleResize)
          window.removeEventListener('resize', handleResize)
        }
      }, [divRef, handleResize])


    const getTotalSum = (sum, i) => {
        let newSum = totalSum
        console.log(totalSum, totalSum + sum)
        setTotalSum(totalSum + sum)
    }
    return(
        <div className="menu-container">
            <div className="menu">
                <div>
                    <h1 className="logo">E-COMM</h1>
                </div>
                <div ref={divRef} className="menu-items">
                    {
                        menuItems.map((ele, i) => {
                            return(
                                <MenuItem index={i} item={ele} getTotalSum={getTotalSum}/>
                            )
                        })

                    }
                    {moreItems.length > 0 && <div>{'More'}</div> }
                </div>

                <div>
                    <div className="search-box">
                        <FontAwesomeIcon icon={faMagnifyingGlass} color={'grey'}/>
                        <input type={'text'} className="search-input"/>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Menu
