// src/components/Navigation.js
import React, { useState, useEffect } from 'react';
import '../styles/index.css';

const Navigation = () => {
  const initialMenuItems = [
    "Home", "About", "Services", "Portfolio", "Contact",
  ];

  const [menuItems, setMenuItems] = useState(initialMenuItems);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const handleResize = () => {
    setWindowWidth(window.innerWidth);
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    // Adjust the menu items when the window width changes
    if (windowWidth < 768) {
      const itemsToShow = initialMenuItems.slice(0, 3);
      setMenuItems(itemsToShow);
    } else {
      setMenuItems(initialMenuItems);
    }
  }, [windowWidth]);

  return (
    <nav className="navigation">
      <ul className="menu">
        {menuItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
        {initialMenuItems.length > menuItems.length && (
          <li className="more">
            <span>More</span>
            <ul className="more-menu">
              {initialMenuItems.slice(menuItems.length).map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navigation;
