// src/components/Navigation.js
import React, { useState, useEffect, useRef } from 'react';
import '../styles/index.css';

const Navigation = () => {
  const initialMenuItems = [
    "Home", "About", "Services", "Portfolio", "Contact",
  ];

  const [menuItems, setMenuItems] = useState(initialMenuItems);
  const [moreItems, setMoreItems] = useState([]);
  const menuRef = useRef(null);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const handleResize = () => {
    setWindowWidth(window.innerWidth);
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    const calculateMenuItems = () => {
      if (menuRef.current) {
        const availableSpace = menuRef.current.offsetWidth;
        const totalWidth = menuRef.current.scrollWidth;
        let itemsToShow = initialMenuItems.slice();

        while (totalWidth > availableSpace && itemsToShow.length > 1) {
          const lastItem = itemsToShow.pop();
          setMoreItems((prevMoreItems) => [lastItem, ...prevMoreItems]);
        }

        setMenuItems(itemsToShow);
      }
    };

    calculateMenuItems();
  }, [windowWidth]);

  return (
    <nav className="navigation">
      <ul className="menu" ref={menuRef}>
        {menuItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
        {moreItems.length > 0 && (
          <li className="more">
            <span>More</span>
            <ul className="more-menu">
              {moreItems.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navigation;
